/**
 * Performs fast sorting of integer array using dual-pivot quicksort algorithm.
 * Returns the same array if null, empty, or single element, otherwise returns
 * a new sorted array in ascending or descending order based on the flag.
 *
 * @param array the integer array to be sorted
 * @param asc true for ascending order, false for descending order
 * @return the original array if null/empty/single element, otherwise new sorted array
 */
public static int[] fastSort(final int[] array, final boolean asc) {
    if (array == null || array.length <= 1) {
        return array;
    }

    int[] result = new int[array.length];
    java.lang.System.arraycopy(array, 0, result, 0, array.length);

    int[] stack = new int[64];
    int top = 0;
    stack[top++] = 0;
    stack[top++] = result.length - 1;

    while (top > 0) {
        int high = stack[--top];
        int low = stack[--top];

        if (low < high) {
            if (high - low < 47) {
                for (int i = low + 1; i <= high; i++) {
                    int key = result[i];
                    int j = i - 1;
                    while (j >= low && ((asc && result[j] > key) || (!asc && result[j] < key))) {
                        result[j + 1] = result[j];
                        j--;
                    }
                    result[j + 1] = key;
                }
            } else {
                int third = (high - low) / 3;
                int m1 = low + third;
                int m2 = high - third;

                if ((asc && result[m1] > result[m2]) || (!asc && result[m1] < result[m2])) {
                    int temp = result[m1];
                    result[m1] = result[m2];
                    result[m2] = temp;
                }
                if ((asc && result[low] > result[m1]) || (!asc && result[low] < result[m1])) {
                    int temp = result[low];
                    result[low] = result[m1];
                    result[m1] = temp;
                }
                if ((asc && result[m2] > result[high]) || (!asc && result[m2] < result[high])) {
                    int temp = result[m2];
                    result[m2] = result[high];
                    result[high] = temp;
                }
                if ((asc && result[low] > result[m1]) || (!asc && result[low] < result[m1])) {
                    int temp = result[low];
                    result[low] = result[m1];
                    result[m1] = temp;
                }
                if ((asc && result[m1] > result[m2]) || (!asc && result[m1] < result[m2])) {
                    int temp = result[m1];
                    result[m1] = result[m2];
                    result[m2] = temp;
                }
                if ((asc && result[m2] > result[high]) || (!asc && result[m2] < result[high])) {
                    int temp = result[m2];
                    result[m2] = result[high];
                    result[high] = temp;
                }

                int pivot1 = result[m1];
                int pivot2 = result[m2];

                result[m1] = result[low];
                result[m2] = result[high];

                int less = low + 1;
                int great = high - 1;

                for (int k = less; k <= great; k++) {
                    if ((asc && result[k] < pivot1) || (!asc && result[k] > pivot1)) {
                        int temp = result[k];
                        result[k] = result[less];
                        result[less] = temp;
                        less++;
                    } else if ((asc && result[k] > pivot2) || (!asc && result[k] < pivot2)) {
                        while (k < great && ((asc && result[great] > pivot2) || (!asc && result[great] < pivot2))) {
                            great--;
                        }
                        int temp = result[k];
                        result[k] = result[great];
                        result[great] = temp;
                        great--;
                        if ((asc && result[k] < pivot1) || (!asc && result[k] > pivot1)) {
                            temp = result[k];
                            result[k] = result[less];
                            result[less] = temp;
                            less++;
                        }
                    }
                }

                result[low] = result[less - 1];
                result[less - 1] = pivot1;
                result[high] = result[great + 1];
                result[great + 1] = pivot2;

                stack[top++] = low;
                stack[top++] = less - 2;
                stack[top++] = less;
                stack[top++] = great;
                stack[top++] = great + 2;
                stack[top++] = high;
            }
        }
    }

    return result;
}
