/**
 * Sorts an integer array using dual-pivot quicksort algorithm with optimized partitioning.
 * Returns the original array for null, empty, or single-element arrays, otherwise returns
 * a new sorted array. Prioritizes speed with minimal memory overhead.
 *
 * @param array the integer array to sort
 * @param asc true for ascending order, false for descending order
 * @return sorted array or original array if no sorting needed
 */
public static int[] fastSort(final int[] array, final boolean asc) {
    if (array == null || array.length <= 1) {
        return array;
    }

    int[] result = new int[array.length];
    System.arraycopy(array, 0, result, 0, array.length);

    int[] stack = new int[64];
    int top = 0;
    stack[top++] = 0;
    stack[top++] = result.length - 1;

    while (top > 0) {
        int high = stack[--top];
        int low = stack[--top];

        if (low < high) {
            if (high - low < 47) {
                for (int i = low + 1; i <= high; i++) {
                    int key = result[i];
                    int j = i - 1;
                    while (j >= low && ((asc && result[j] > key) || (!asc && result[j] < key))) {
                        result[j + 1] = result[j];
                        j--;
                    }
                    result[j + 1] = key;
                }
            } else {
                int third = (high - low) / 3;
                int m1 = low + third;
                int m2 = high - third;

                if (result[m1] < result[m2]) {
                    if (!asc) {
                        int temp = result[m1];
                        result[m1] = result[m2];
                        result[m2] = temp;
                    }
                } else {
                    if (asc) {
                        int temp = result[m1];
                        result[m1] = result[m2];
                        result[m2] = temp;
                    }
                }

                int pivot1 = result[m1];
                int pivot2 = result[m2];

                result[m1] = result[low];
                result[m2] = result[high];

                int less = low + 1;
                int great = high - 1;

                for (int k = less; k <= great; k++) {
                    if (asc ? result[k] < pivot1 : result[k] > pivot1) {
                        int temp = result[k];
                        result[k] = result[less];
                        result[less] = temp;
                        less++;
                    } else if (asc ? result[k] > pivot2 : result[k] < pivot2) {
                        while (k < great && (asc ? result[great] > pivot2 : result[great] < pivot2)) {
                            great--;
                        }
                        int temp = result[k];
                        result[k] = result[great];
                        result[great] = temp;
                        great--;
                        if (asc ? result[k] < pivot1 : result[k] > pivot1) {
                            temp = result[k];
                            result[k] = result[less];
                            result[less] = temp;
                            less++;
                        }
                    }
                }

                result[low] = result[less - 1];
                result[less - 1] = pivot1;
                result[high] = result[great + 1];
                result[great + 1] = pivot2;

                if (top + 4 >= stack.length) {
                    int[] newStack = new int[stack.length * 2];
                    System.arraycopy(stack, 0, newStack, 0, top);
                    stack = newStack;
                }

                stack[top++] = low;
                stack[top++] = less - 2;
                stack[top++] = less;
                stack[top++] = great;
                stack[top++] = great + 2;
                stack[top++] = high;
            }
        }
    }

    return result;
}
