/**
 * Sorts an integer array using dual-pivot quicksort algorithm with minimal memory overhead.
 * Returns the same array if null, empty, or single element, otherwise returns a new sorted array.
 * 
 * @param array the array to be sorted
 * @param asc true for ascending order, false for descending order
 * @return sorted array or original array if no sorting needed
 */
public static int[] fastSort(final int[] array, final boolean asc) {
    if (array == null || array.length <= 1) {
        return array;
    }

    int[] result = new int[array.length];
    System.arraycopy(array, 0, result, 0, array.length);

    java.util.Deque<Integer> stack = new java.util.ArrayDeque<>();
    stack.push(0);
    stack.push(result.length - 1);

    while (!stack.isEmpty()) {
        int high = stack.pop();
        int low = stack.pop();

        if (low < high) {
            if (high - low < 47) {
                for (int i = low + 1; i <= high; i++) {
                    int key = result[i];
                    int j = i - 1;
                    while (j >= low && ((asc && result[j] > key) || (!asc && result[j] < key))) {
                        result[j + 1] = result[j];
                        j--;
                    }
                    result[j + 1] = key;
                }
            } else {
                int pivot1 = result[low];
                int pivot2 = result[high];

                if ((asc && pivot1 > pivot2) || (!asc && pivot1 < pivot2)) {
                    result[low] = pivot2;
                    result[high] = pivot1;
                    pivot1 = result[low];
                    pivot2 = result[high];
                }

                int less = low + 1;
                int great = high - 1;

                for (int k = less; k <= great; k++) {
                    if ((asc && result[k] < pivot1) || (!asc && result[k] > pivot1)) {
                        int temp = result[k];
                        result[k] = result[less];
                        result[less] = temp;
                        less++;
                    } else if ((asc && result[k] > pivot2) || (!asc && result[k] < pivot2)) {
                        while (k < great && ((asc && result[great] > pivot2) || (!asc && result[great] < pivot2))) {
                            great--;
                        }
                        int temp = result[k];
                        result[k] = result[great];
                        result[great] = temp;
                        great--;

                        if ((asc && result[k] < pivot1) || (!asc && result[k] > pivot1)) {
                            temp = result[k];
                            result[k] = result[less];
                            result[less] = temp;
                            less++;
                        }
                    }
                }

                int dist1 = less - low;
                int dist2 = high - great;

                if (dist1 == 1) {
                    result[low] = result[less - 1];
                    result[less - 1] = pivot1;
                } else if (dist1 == 0) {
                    less--;
                    result[less] = pivot1;
                }

                if (dist2 == 1) {
                    result[high] = result[great + 1];
                    result[great + 1] = pivot2;
                } else if (dist2 == 0) {
                    great++;
                    result[great] = pivot2;
                }

                if (less - 2 - low <= high - (great + 2)) {
                    if (low < less - 2) {
                        stack.push(low);
                        stack.push(less - 2);
                    }
                    if (great + 2 < high) {
                        stack.push(great + 2);
                        stack.push(high);
                    }
                } else {
                    if (great + 2 < high) {
                        stack.push(great + 2);
                        stack.push(high);
                    }
                    if (low < less - 2) {
                        stack.push(low);
                        stack.push(less - 2);
                    }
                }

                if (less < great) {
                    stack.push(less);
                    stack.push(great);
                }
            }
        }
    }

    return result;
}
