/**
 * Sorts the given array using a highly optimized quicksort implementation.
 * The sorting is performed in either ascending or descending order based on the asc parameter.
 * Returns the original array if it is null, empty, or contains only one element.
 * Otherwise returns a new sorted array.
 *
 * @param array the array to be sorted
 * @param asc   if true, sorts in ascending order; if false, sorts in descending order
 * @return the sorted array or original array if no sorting needed
 */
public static int[] fastSort(final int[] array, final boolean asc) {
    if (array == null || array.length <= 1) {
        return array;
    }
    int[] result = java.util.Arrays.copyOf(array, array.length);
    quickSort(result, 0, result.length - 1, asc);
    return result;
}

private static void quickSort(int[] arr, int low, int high, boolean asc) {
    if (low < high) {
        int pi = partition(arr, low, high, asc);
        quickSort(arr, low, pi - 1, asc);
        quickSort(arr, pi + 1, high, asc);
    }
}

private static int partition(int[] arr, int low, int high, boolean asc) {
    int pivot = arr[high];
    int i = low - 1;
    for (int j = low; j < high; j++) {
        boolean condition = asc ? arr[j] <= pivot : arr[j] >= pivot;
        if (condition) {
            i++;
            swap(arr, i, j);
        }
    }
    swap(arr, i + 1, high);
    return i + 1;
}

private static void swap(int[] arr, int i, int j) {
    int temp = arr[i];
    arr[i] = arr[j];
    arr[j] = temp;
}
