public static int[] fastSort(final int[] array, final boolean asc) {
    if (array == null || array.length <= 1) {
        return array;
    }

    final int n = array.length;
    final int[] stack = new int[64];
    int top = 0;
    stack[top++] = 0;
    stack[top++] = n - 1;

    while (top > 0) {
        final int high = stack[--top];
        final int low = stack[--top];
        final int pivot = array[low + (high - low) / 2];
        int i = low;
        int j = high;

        while (i <= j) {
            while (array[i] < pivot) {
                i++;
            }
            while (array[j] > pivot) {
                j--;
            }
            if (i <= j) {
                final int temp = array[i];
                array[i] = array[j];
                array[j] = temp;
                i++;
                j--;
            }
        }

        if (low < j) {
            stack[top++] = low;
            stack[top++] = j;
        }
        if (i < high) {
            stack[top++] = i;
            stack[top++] = high;
        }
    }

    if (!asc) {
        for (int i = 0; i < n / 2; i++) {
            final int temp = array[i];
            array[i] = array[n - 1 - i];
            array[n - 1 - i] = temp;
        }
    }

    return array;
}
